import { useState } from "react";

function SelBar({option1, option2}) {
    function handleClickLeft(e) {
        e.preventDefault();
        console.log('You clicked option 1');
        if (tab === 2) setTab(1);
    }

    function handleClickRight(e) {
        e.preventDefault();
        console.log('You clicked option 2');
        if (tab === 1) setTab(2);
    }

    const [tab,setTab] = useState(1)

    return (
        <div className="select-bar-container">
            <a href="https://css-tricks.com/snippets/jquery/make-entire-div-clickable/">
                <div className="otheroption" onClick={handleClickLeft}>
                    <h1 className="insidetext noselect">{option1}</h1>
                </div>
            </a>
            <a href="https://css-tricks.com/snippets/jquery/make-entire-div-clickable/">
                <div className="currentoption" onClick={handleClickRight}>
                    <h1 className="insidetext noselect">{option2}</h1>
                </div>
            </a>
            
        </div>
    );
}
   
export default SelBar;
