import { useState } from "react";
import Table from "./Table";
import Button from 'react-bootstrap/Button';
import SaveModal from '../Modal/SaveModal.js';

function TableTab() {
    const [show, setShow] = useState(false);
    const data = [
        { tableNo: 1, tableStatus: "occupied" }, { tableNo: 2, tableStatus: "occupied" }, { tableNo: 3, tableStatus: "occupied" }, { tableNo: 4, tableStatus: "unoccupied" }, { tableNo: 5, tableStatus: "occupied" },
        { tableNo: 6, tableStatus: "unoccupied" }, { tableNo: 7, tableStatus: "unoccupied" }, { tableNo: 8, tableStatus: "unoccupied" }, { tableNo: 9, tableStatus: "occupied" }, { tableNo: 10, tableStatus: "unoccupied" },
        { tableNo: 11, tableStatus: "occupied" }, { tableNo: 12, tableStatus: "occupied" }, { tableNo: 13, tableStatus: "occupied" }, { tableNo: 14, tableStatus: "unoccupied" }, { tableNo: 15, tableStatus: "unoccupied" },
        { tableNo: 16, tableStatus: "unoccupied" }, { tableNo: 17, tableStatus: "unoccupied" }, { tableNo: 18, tableStatus: "unoccupied" }, { tableNo: 19, tableStatus: "occupied" }, { tableNo: 20, tableStatus: "unoccupied" },
        { tableNo: 21, tableStatus: "occupied" }, { tableNo: 22, tableStatus: "unoccupied" }, { tableNo: 23, tableStatus: "unoccupied" }, { tableNo: 24, tableStatus: "occupied" }, { tableNo: 25, tableStatus: "unoccupied" }
    ];
    const [tables, setTables] = useState(data);
    const [changes, setChanges] = [
        false,false,false,false,false,
        false,false,false,false,false,
        false,false,false,false,false,
        false,false,false,false,false,
        false,false,false,false,false
    ];

    function saveData(){
        console.log(changes);
        setShow(false);
    }

    function handleClose(){
        setShow(false);
    }

    function handleShow(){
        setShow(true);
    }

    function callBack(tableNo){
        setChanges(changes[tableNo - 1] = !changes[tableNo - 1]);
    }

    return (
        <div className="content">
            <SaveModal show={show} handleClose={handleClose} saveData={saveData}></SaveModal>
            <nav className="header">
                <h1 className="noselect">Table List</h1>
                <Button variant="outline-dark" onClick={handleShow}>Save</Button>
            </nav>
            <div className="tableList">
                {tables.map((table/*Each Table*/) => (
                    <Table tableNo={table.tableNo} tableStatus={table.tableStatus} callBack={() => callBack(table.tableNo)} key={table.tableNo}/>
                ))}
            </div>
        </div>
    );
}
   
export default TableTab;
